import { GET_QUIZZES} from '../actionType.js'
import axios from "axios"


export const getQuizzes = ()=>{
    return (dispatch)=>{
        axios.get("/api/quizzes/all").then((res)=>{

            dispatch({
                type: GET_QUIZZES,
                payload: res.data,
            })
        })

    }
}


export const editQById = (id, inputData) => (dispatch) => {
    axios.put(`api/questions/${id}`, inputData).then((res) => dispatch(getQuestions()));
};
